# -*- coding: utf-8 -*-

import ccxt.async_support as ccxt  # noqa: F402
from ccxt.test.exchange.base import test_shared_methods  # noqa E402

def test_keysort():
    exchange = ccxt.Exchange({
        'id': 'sampleexchange',
    })
    assert 'GO_SKIP_START'
    # Test 1: Basic key sorting
    unsorted_dict_1 = {
        'c': 3,
        'a': 1,
        'b': 2,
    }
    expected_sorted_1 = {
        'a': 1,
        'b': 2,
        'c': 3,
    }
    result1 = exchange.keysort(unsorted_dict_1)
    test_shared_methods.assert_deep_equal(exchange, None, 'testKeysort', list(result1.keys()), list(expected_sorted_1.keys()))
    # Test 2: Already sorted dictionary
    unsorted_dict_2 = {
        'alpha': 'first',
        'beta': 'second',
        'gamma': 'third',
    }
    expected_sorted_2 = {
        'alpha': 'first',
        'beta': 'second',
        'gamma': 'third',
    }
    result2 = exchange.keysort(unsorted_dict_2)
    test_shared_methods.assert_deep_equal(exchange, None, 'testKeysort', list(result2.keys()), list(expected_sorted_2.keys()))
    # Test 3: Reverse sorted input
    unsorted_dict_3 = {
        'z': 'last',
        'n': 'middle',
        'a': 'first',
    }
    expected_sorted_3 = {
        'a': 'first',
        'n': 'middle',
        'z': 'last',
    }
    result3 = exchange.keysort(unsorted_dict_3)
    test_shared_methods.assert_deep_equal(exchange, None, 'testKeysort', list(result3.keys()), list(expected_sorted_3.keys()))
    # Test 4: Empty dictionary
    unsorted_dict_4 = {}
    expected_sorted_4 = {}
    result4 = exchange.keysort(unsorted_dict_4)
    test_shared_methods.assert_deep_equal(exchange, None, 'testKeysort', list(result4.keys()), list(expected_sorted_4.keys()))
    # Test 5: Single key dictionary
    unsorted_dict_5 = {
        'only': 'one',
    }
    expected_sorted_5 = {
        'only': 'one',
    }
    result5 = exchange.keysort(unsorted_dict_5)
    test_shared_methods.assert_deep_equal(exchange, None, 'testKeysort', list(result5.keys()), list(expected_sorted_5.keys()))
    # Test 6: Numeric string keys
    unsorted_dict_6 = {
        '10': 'ten',
        '2': 'two',
        '1': 'one',
    }
    expected_sorted_6 = {
        '1': 'one',
        '10': 'ten',
        '2': 'two',
    }
    result6 = exchange.keysort(unsorted_dict_6)
    test_shared_methods.assert_deep_equal(exchange, None, 'testKeysort', list(result6.keys()), list(expected_sorted_6.keys()))
    # Test 7: Mixed case keys (lexicographic sort)
    unsorted_dict_7 = {
        'Banana': 1,
        'apple': 2,
        'Cherry': 3,
    }
    expected_sorted_7 = {
        'Banana': 1,
        'Cherry': 3,
        'apple': 2,
    }
    result7 = exchange.keysort(unsorted_dict_7)
    test_shared_methods.assert_deep_equal(exchange, None, 'testKeysort', list(result7.keys()), list(expected_sorted_7.keys()))
    assert 'GO_SKIP_END'
    assert exchange.safe_string(None, 'placeholder') is None  # go trick
